generated.css:
.reddit_widget__app {
  font-size: 16px;
  padding: 0;
  font-family: IBMPlexSans, Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  color: #222;
  box-shadow: 2px 2px 12px 0 rgba(0, 0, 0, 0.07);
  border-radius: 12px;
  overflow: hidden;
}

.reddit_widget__app a {
  color: #5399d7;
  transition: 0.2s ease-in-out all;
}

.reddit_widget__header {
  margin: 0;
  display: block;
  font-size: 19px;
  padding: 12px 6px;
  background: #5399d7;
  color: white;
  text-align: center;
}

.reddit_widget__header a {
  color: #fff;
}

.reddit_widget__inner {
  padding: 12px;
}

.reddit_widget__inner a {
  color: #222;
  text-decoration: none;
}

.reddit_widget__post {
  display: flex;
  flex-direction: column;
  padding: 6px 0;
}


.reddit_widget__posted_by {
  font-size: 14px;
  color: rgb(120, 124, 126);
  padding: 0 0 6px 0;
}

.reddit_widget__posted_by a {
  color: rgb(120, 124, 126);
}

a.reddit_widget__posted_by:hover {
  text-decoration: underline;
}

a.reddit_widget__title {
  font-weight: 500;
}

a.reddit_widget__title:hover {
  color: #000;
}

.reddit_widget__powered_by {
  text-align: center;
  font-size: 14px;
}

.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
}

.modal-content {
  background-color: white;
  /* padding: 20px; */
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
  text-align: center;
}
